import React, { useState, useEffect } from "react";
import { Alert, Modal, Text, View, TouchableOpacity } from "react-native";
import { useNavigation } from "@react-navigation/core";
import styles from "./styles";
import { typeVehicles } from "../../config/constants";
import { VehicleCardPrimary } from "../../components/VehicleCard";
import { Header } from "../../components/Header";
import { NextArrowButton } from "../../components/Buttons/NextArrowButton";

import actions from "../../actions/todo";

import { useDispatch } from "react-redux";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useAuth } from "../../hooks/auth";

import BtnClose from "../../components/BtnClose";

export function VehicleSelect() {
  const { user } = useAuth();
  const dispatch = useDispatch();

  const [myReports, setMyReports] = useState<any[]>([]);
  const [vehicleChoice, setVehicleChoie] = useState<any>("");
  const [modalVisible, setModalVisible] = useState(false);
  const [loading, setLoading] = useState(false);

  const navigation = useNavigation();

  const handleStart = async (value: string) => {
    const dataKey = `@laudos_user:${user.id}`;
    const data = await AsyncStorage.getItem(dataKey);
    if (data) {
      setLoading(true);
    }
    const reportsStoraged = data ? (JSON.parse(data) as any) : [];
    let reportsFiltered;
    if (reportsStoraged) {
      reportsFiltered = await reportsStoraged.filter((item: any) => {
        console.log(item.LaudoVeicular.statusDoLaudo);
        console.log(item.LaudoVeicular.statusDoLaudo.completo);
        console.log(item.LaudoVeicular.statusDoLaudo.oculto);

        item.LaudoVeicular.statusDoLaudo.completo == false &&
          item.LaudoVeicular.statusDoLaudo.oculto == false;
      });
    }
    console.log(reportsStoraged.length);
    setMyReports(reportsFiltered);
    setLoading(false);
    setVehicleChoie(value);
    dispatch(actions.addTypeVehicle(vehicleChoice));

    setModalVisible(true);
  };

  const handleViewReports = () => {
    navigation.navigate("MyReports");
  };

  const handleContinueReport = (data: any) => {
    dispatch(actions.resetDataState(data));
    setModalVisible(!modalVisible);
    navigation.navigate("NewReport");
  };
  const handleNewReport = () => {
    dispatch(actions.addTypeVehicle(vehicleChoice));
    navigation.navigate("NewReport");
  };

  const ModalReport = () => {
    return (
      <View>
        <Modal
          animationType="slide"
          transparent={true}
          visible={modalVisible}
          onRequestClose={() => {
            setModalVisible(!modalVisible);
          }}
        >
          <View>
            <View style={styles.modalView}>
              <View>
                <BtnClose onPress={() => setModalVisible(false)} />
              </View>
              {myReports.length > 0 && loading ? (
                <>
                  <Text style={styles.modalText}>Laudos incompletos</Text>
                  <Text style={styles.modalText}>
                    Clique no qual deseja continuar:
                  </Text>

                  {/* <ScrollView style={styles.modalListItem}> */}
                  <View style={styles.modalListItem}>
                    {myReports.map((item: any, index: number) => (
                      <TouchableOpacity
                        key={index}
                        onPress={() => handleContinueReport(item)}
                      >
                        <Text>
                          {" "}
                          <Text>{index + 1} - </Text>
                          {item.LaudoVeicular.Data.Cabecalho.Rep}
                        </Text>
                      </TouchableOpacity>
                    ))}
                  </View>
                </>
              ) : (
                <>
                  <Text style={styles.modalText}>Clique para continuar</Text>
                </>
              )}
              <TouchableOpacity
                style={[styles.button, styles.buttonClose]}
                onPress={() => {
                  setModalVisible(!modalVisible);
                  handleNewReport();
                }}
              >
                <Text style={styles.textStyle}>Criar um novo</Text>
              </TouchableOpacity>
            </View>
          </View>
        </Modal>
      </View>
    );
  };

  // useEffect(() => {
  //   setLoading(true);
  //   async function loadStorageData() {
  //     const dataKey = `@laudos_user:${user.id}`;
  //     const data = await AsyncStorage.getItem(dataKey);
  //     const reportsStoraged = data ? (JSON.parse(data) as any) : [];
  //     let reportsFiltered;
  //     if (reportsStoraged) {
  //       reportsFiltered = await reportsStoraged.filter(
  //         (item: any) =>
  //           item.LaudoVeicular.statusDoLaudo.completo == false &&
  //           item.LaudoVeicular.statusDoLaudo.oculto == false
  //       );
  //     }
  //     setMyReports(reportsFiltered);
  //     setLoading(false);
  //   }
  //   loadStorageData();
  // }, []);

  return (
    <>
      <View style={styles.container}>
        <View>
          <Header></Header>
        </View>

        <View>
          <Text style={styles.title}> Escolha um ve√≠culo</Text>
        </View>
        <View style={styles.containerModal}>
          <ModalReport />
        </View>

        <View style={styles.vehicles}>
          <View style={styles.cards}>
            {typeVehicles.map(({ title, icone, available }, index) => (
              <VehicleCardPrimary
                key={`VehicleCardPrimary-${index}`}
                title={title}
                icone={icone}
                available={available}
                onPress={() => {
                  handleStart(title);
                }}
              />
            ))}
          </View>
          <NextArrowButton
            title="Ver Laudos"
            icone="upload-cloud"
            onPress={handleViewReports}
            isValid={true}
          />
        </View>
      </View>
    </>
  );
}
